import React,{useState, useEffect} from 'react'
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Modal from 'react-bootstrap/Modal';
import PubSub from 'pubsub-js'

export default function List() {

  const [todoList, setList] = useState([
    {id: '1', title: 'todo item 1', detail: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.', isDone: true},
    {id: '2', title: 'todo item 2', detail: 'todo item detail 2', isDone: false},
    {id: '3', title: 'todo item 3', detail: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', isDone: false},
  ])
  const [show, setShow] = useState(false);

  useEffect(() => {
    let dataFromInput = function (_, data) {
      const uuid = window.self.crypto.randomUUID()
      setList([{...data, id: uuid, isDone: false}, ...todoList])
    };
    PubSub.subscribe('todoItem', dataFromInput);
  }, [todoList])

  function switchChecked(id, current){
    const newList = todoList.map(item => {
      if(item.id === id){
       item['isDone'] = current.target.checked
      }
      return item
    })
    setList(newList);
  }

  function deleteTodo(){

  }

  return (
    <>
      {
        todoList.map((item, _) => {
          return (
            <Card key={item.id} className='mb-1'>
              <Card.Header as="h6">
                status: 
                {' '}
                {item.isDone? 'Completed' : 'Outstanding'}
                <Form.Check 
                  className='d-inline mt-1 float-end'
                  aria-label={item.title} 
                  defaultChecked={item.isDone} 
                  onChange={ current => switchChecked(item.id, current) }
                />
              </Card.Header>
              <Card.Body>
                <Card.Title>{item.title}</Card.Title>
                <Card.Text>
                {item.detail}
                </Card.Text>
                <Button variant="danger" onClick={deleteTodo}>Delete</Button>
              </Card.Body>
            </Card>
          )
        })
      }
      {
        show? (
          <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
        )
        : null
      }
    </>
  )
}
