import React,{useState, useEffect} from 'react'
import Accordion from 'react-bootstrap/Accordion';
import Form from 'react-bootstrap/Form';
import Stack from 'react-bootstrap/Stack';
import Button from 'react-bootstrap/Button';
import PubSub from 'pubsub-js'

export default function List() {

  const [todoList, setList] = useState([
    {id: '1', title: 'todo item 1', detail: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.', isDone: true},
    {id: '2', title: 'todo item 2', detail: 'todo item detail 2', isDone: false}
  ])

  useEffect(() => {
    let dataFromInput = function (_, data) {
      const uuid = window.self.crypto.randomUUID()
      setList([{...data, id: uuid, isDone: false}, ...todoList])
    };
    PubSub.subscribe('todoItem', dataFromInput);
  }, [todoList])

  function switchChecked(id, current){
    const newList = todoList.map(item => {
      if(item.id === id){
       item['isDone'] = current.target.checked
      }
      return item
    })
    setList(newList);
  }

  return (
    <>
      <Accordion>
        {
          todoList.map((item, _) => {
            return (
              <Stack direction="horizontal" className='mb-1' gap={2} key={item.id}>
                <Form.Check 
                  aria-label={item.title} 
                  defaultChecked={item.isDone} 
                  onChange={ current => switchChecked(item.id, current) }
                />
                <Accordion.Item eventKey={item.id} className='flex-grow-1'>
                  <Accordion.Header>{item.title}</Accordion.Header>
                  <Accordion.Body style={{overflowWrap: 'anywhere'}}>
                    {item.detail}
                  </Accordion.Body>
                </Accordion.Item>
                <Button variant="danger" size="lg">
                  Delete
                </Button>
              </Stack>
            )
          })
        }
      </Accordion>
    </>
  )
}
