import React,{useState, useEffect} from 'react'
import Accordion from 'react-bootstrap/Accordion';
import Form from 'react-bootstrap/Form';
import Stack from 'react-bootstrap/Stack';
import PubSub from 'pubsub-js'

export default function List() {

  const [todoList, setList] = useState([
    {id: '1', title: 'todo item 1', detail: 'todo item detail 1', isDone: true},
    {id: '2', title: 'todo item 2', detail: 'todo item detail 2', isDone: false}
  ])

  useEffect(() => {
    let dataFromInput = function (_, data) {
      const uuid = window.self.crypto.randomUUID()
      setList([{...data, id: uuid, isDone: false}, ...todoList])
    };
    PubSub.subscribe('todoItem', dataFromInput);
  }, [todoList])

  function switchChecked(id, current){
    console.log(id, current.target.checked);
    const newItem = todoList.filter(item => {
      return item.id === id
    })
    newItem[0]['isDone'] = current.target.checked
    console.log();
  }

  return (
    <>
      <Accordion>
        {
          todoList.map((item, _) => {
            return (
              <Stack direction="horizontal" className='mb-1' gap={2} key={item.id}>
                <Form.Check 
                  aria-label={item.title} 
                  defaultChecked={item.isDone} 
                  onChange={ current => switchChecked(item.id, current) }
                />
                <Accordion.Item eventKey={item.id} className='flex-grow-1'>
                  <Accordion.Header>{item.title}</Accordion.Header>
                  <Accordion.Body>
                    {item.detail}
                  </Accordion.Body>
                </Accordion.Item>
              </Stack>
            )
          })
        }
      </Accordion>
    </>
  )
}
