import React, {useState} from 'react'
import { Button } from 'react-bootstrap';
import Form from 'react-bootstrap/Form';
import Stack from 'react-bootstrap/Stack';
import Alert from 'react-bootstrap/Alert';

export default function Input() {

  const [show, setShow] = useState(true);
  const [mission,setMission] = useState({
    title: '',
    detail: ''
  })

  function amendMission(type, e){
    setMission(
      {...mission, [type]: e.target.value}
    )
  }

  function addTodo(){
    let clearMission = {}
    for(let key in mission){
      clearMission[key] = ''
    }
    setMission(clearMission)

    if(mission.title.trim() !== ''){
      console.log('can add');
    }
  }

  return (
    <>
      <h1 className='mt-5'>Todo List</h1>
      <Stack gap={2} className="mx-auto mt-2">
        <Form.Control 
          className="me-auto"
          placeholder="Input your mission title"
          value={mission.title} 
          onChange={(e) => {amendMission('title', e)}}
        />
        <Form.Control 
          className="me-auto" 
          as="textarea" 
          placeholder="Input your mission detail" 
          value={mission.detail} 
          onChange={(e) => {amendMission('detail', e)}}
        />
        <Button 
          variant="success"
          onClick={addTodo}
        >Submit</Button>
      </Stack>

      <Alert variant="danger" onClose={() => setShow(false)} dismissible>
        <Alert.Heading>Oh snap! You got an error!</Alert.Heading>
        <p>
          Change this and that and try again. Duis mollis, est non commodo
          luctus, nisi erat porttitor ligula, eget lacinia odio sem nec elit.
          Cras mattis consectetur purus sit amet fermentum.
        </p>
      </Alert>
    </>
  )
}
