import React, {useState} from 'react'
import { Button } from 'react-bootstrap';
import Form from 'react-bootstrap/Form';
import Stack from 'react-bootstrap/Stack';

export default function Input() {

  const [mission,setMission] = useState({
    title: '',
    detail: ''
  })

  function amendValue(type, e){
    console.log(type, e.target.value);
    setMission({...mission, [type]: e.target.value})
  }

  return (
    <>
      <h1 className='mt-5'>Todo List</h1>
      <Stack gap={2} className="mx-auto mt-2">
        <Form.Control className="me-auto" placeholder="Add your mission title" value={mission.title} onChange={(e) => {amendValue('title', e)}}/>
        <Form.Control className="me-auto" as="textarea" placeholder="Add your mission detail" value={mission.detail} onChange={(e) => {amendValue('detail', e)}}/>
        <Button variant="secondary">Submit</Button>
      </Stack>
    </>
  )
}
